#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\workplace\temp\WEB\LucidFlow\backend\node_modules\vite\bin\node_modules;D:\workplace\temp\WEB\LucidFlow\backend\node_modules\vite\node_modules;D:\workplace\temp\WEB\LucidFlow\backend\node_modules;D:\workplace\temp\WEB\LucidFlow\node_modules;D:\workplace\temp\WEB\node_modules;D:\workplace\temp\node_modules;D:\workplace\node_modules;D:\node_modules;D:\workplace\temp\WEB\LucidFlow\backend\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/workplace/temp/WEB/LucidFlow/backend/node_modules/vite/bin/node_modules:/mnt/d/workplace/temp/WEB/LucidFlow/backend/node_modules/vite/node_modules:/mnt/d/workplace/temp/WEB/LucidFlow/backend/node_modules:/mnt/d/workplace/temp/WEB/LucidFlow/node_modules:/mnt/d/workplace/temp/WEB/node_modules:/mnt/d/workplace/temp/node_modules:/mnt/d/workplace/node_modules:/mnt/d/node_modules:/mnt/d/workplace/temp/WEB/LucidFlow/backend/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../vite/bin/vite.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../vite/bin/vite.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../vite/bin/vite.js" $args
  } else {
    & "node$exe"  "$basedir/../vite/bin/vite.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
